// Get all jobs from API
const allJobs = $input.first().json;

// Keywords to search for (case-insensitive)
const keywords = ['qa', 'quality assurance', 'testing', 'test engineer', 
                  'etl', 'data testing', 'database', 'quality engineer',
                  'sdet', 'automation', 'manual testing', 'data quality'];

// Filter and map jobs
const relevantJobs = [];

for (const job of allJobs) {
  // Skip if not a job object (first item is metadata)
  if (!job.position || !job.company) continue;

  // Combine searchable text
  const searchText = (
    (job.position || '') + ' ' + 
    (job.description || '') + ' ' + 
    (job.tags || []).join(' ')
  ).toLowerCase();

  // Check if any keyword matches
  const matches = keywords.some(keyword => searchText.includes(keyword));

  if (matches) {
    relevantJobs.push({
      json: {
        id: job.id || job.slug,
        title: job.position,
        company: job.company,
        url: `https://remoteok.com/remote-jobs/${job.slug || job.id}`,
        location: job.location || 'Remote',
        posted_date: job.date || new Date().toISOString().split('T')[0],
        description: (job.description || '').substring(0, 2000),
        tags: (job.tags || []).join(', '),
        salary: job.salary_max ? `$${job.salary_max}` : 'Not specified',
        source: 'RemoteOK',
        scraped_at: new Date().toISOString(),
        status: 'new',
        match_score: null,
        applied: false
      }
    });
  }
}

console.log(`Found ${relevantJobs.length} relevant jobs out of ${allJobs.length} total`);

return relevantJobs;
